<?php include("head.php"); if($_GET){	if(isset($_GET['delete_id'])){		$id=$_GET['delete_id'];		$query="DELETE FROM angajati WHERE AngajatID=".$id."";		$connection->query($query);		$query="DELETE FROM angserv WHERE AngajatID=".$id."";		$connection->query($query);	}	}if(isset($_POST['adaug'])){	if($_POST['nume']!=""&&$_POST['prenume']!=""){		$nume=$_POST['nume'];		$prenume=$_POST['prenume'];		$id=$_POST['id'];		$query="INSERT INTO angajati (Nume,Prenume) VALUES ('".$nume."','".$prenume."')";		if($connection->query($query)===TRUE){			$aid=$connection->insert_id;			$query="INSERT INTO angserv (ServiceID,AngajatID) VALUES ('".$id."','".$aid."')";		$connection->query($query);		}					}	}	if(isset($_POST['edit'])){	if($_POST['nume']!=""&&$_POST['prenume']!=""){		$nume=$_POST['nume'];		$prenume=$_POST['prenume'];		$id=$_POST['id'];		$aid=$_POST['aid'];		$query="UPDATE angajati SET Nume='".$nume."',Prenume='".$prenume."' WHERE AngajatID=".$aid."";		$connection->query($query);		$query="UPDATE angserv SET ServiceID=".$id." WHERE AngajatID=".$aid."";		$connection->query($query);	}	}?><form action="Angajati.php" method="post">Nume:<input type="text" name="nume"/>Prenume:<input type="text"  name="prenume" />Serviciu:<select name="id"><?php    $query="SELECT ServiceID,Denumire FROM servicii";$result=$connection->query($query);if($result->num_rows>0){	while($row=$result->fetch_assoc()){		$output="<option value='".$row['ServiceID']."'>";		$output.=$row['Denumire'];		$output.="</option>";		echo($output);	}}?></select><input type="submit"  name="adaug" value="+"/></form><form action="Angajati.php" method="post">Angajat:<select name="aid"><?php    $query="SELECT AngajatID,Nume,Prenume FROM angajati";$result=$connection->query($query);if($result->num_rows>0){	while($row=$result->fetch_assoc()){		$output="<option value='".$row['AngajatID']."'>";		$output.=$row['Nume']." ".$row['Prenume'];		$output.="</option>";		echo($output);	}}?></select>Serviciu:<select name="id"><?php    $query="SELECT ServiceID,Denumire FROM servicii";$result=$connection->query($query);if($result->num_rows>0){	while($row=$result->fetch_assoc()){		$output="<option value='".$row['ServiceID']."'>";		$output.=$row['Denumire'];		$output.="</option>";		echo($output);	}}?></select>Nume:<input type="text" name="nume"/>Prenume:<input type="text"  name="prenume"/><input type="submit"  name="edit" value="Edit"/></form><br /><table border="0"><tr><th>Nume</th><th>Serviciu</th><th>Delete</th></tr><?php$query="SELECT  a.Denumire,b.Nume,b.Prenume,b.AngajatIDFROM  servicii a  JOIN angserv ba ON a.ServiceID = ba.ServiceID  JOIN angajati b ON b.AngajatID = ba.AngajatID";$result=$connection->query($query);if($result->num_rows>0){	while($row=$result->fetch_assoc()){		$output="<tr><td>";		$output.=$row['Nume']." ".$row['Prenume'];		$output.="</td><td>";		$output.=$row['Denumire'];		$output.="</td><td>";		$output.="<button><a href='Angajati.php?delete_id=".$row['AngajatID']."'>X</a></button>";		$output.="</td></tr>";		echo($output);	}}?></table><br><?php include("footer.php"); ?>